import { Component, OnInit, ViewChild } from '@angular/core';
import { StylesMap } from './map-track.style';
import { StylesMarker } from './map-track.markers.style';
import { MapInfoWindow, MapMarker, MapDirectionsService } from '@angular/google-maps';
//import { MapTrackService } from '../../../shared/services/map.track.service'
import { map, Observable } from 'rxjs';


@Component({
  selector: 'app-map-track',
  templateUrl: './map-track.component.html',
  styleUrls: ['./map-track.component.css']
})
export class MapTrackComponent implements OnInit {
  @ViewChild(MapInfoWindow) infoWindow: MapInfoWindow | undefined;

  mapDirectionsService: MapDirectionsService;


  zoom = 8;
  center: google.maps.LatLngLiteral = {
    lat: 49.9,
    lng: 36.2
  };

  options: google.maps.MapOptions = {
    disableDefaultUI: true,
    styles: StylesMap.dark,
  };

  markerOptions: google.maps.MarkerOptions = {
    draggable: false
  };

  markerStyle = StylesMarker.machine;

  markerPositions: google.maps.LatLngLiteral[] = [];

  directionsResults$!: Observable<google.maps.DirectionsResult | undefined>;
  

  ngOnInit(): void {
   
  }

  constructor(mapDirectionsService: MapDirectionsService) {
    this.mapDirectionsService = mapDirectionsService;
  }


  openInfoWindow(marker: MapMarker) {
    if (this.infoWindow != undefined) this.infoWindow.open(marker);
  }
  display: any;
  move(event: google.maps.MapMouseEvent) {
    if (event.latLng != null) this.display = event.latLng.toJSON();
  }



}
<div class="simple-container">
  <div class="map">
    <google-map
                width="100%"
                height="90vh"
                (mapMousemove)="move($event)"
                [options]="options"
                [center]="center"
                [zoom]="zoom">

      <map-marker *ngFor="let markerPosition of markerPositions"
                  [position]="markerPosition"
                  [options]="markerStyle"></map-marker>

      <map-info-window>Info Window content</map-info-window>

      <!--<map-ground-overlay [url]="imageUrl"
                      [bounds]="imageBounds"></map-ground-overlay>-->
      <map-directions-renderer *ngIf="(directionsResults$ | async) as directionsResults"
                               [directions]="directionsResults"></map-directions-renderer>

      <!--<map-kml-layer [url]="kmlUrl"></map-kml-layer>-->
    </google-map>
   
    <div>Latitude: {{display?.lat}}</div>
    <div>Longitude: {{display?.lng}}</div>
  </div>
</div>
